var UnoAdmin = new function()
{
    this.init = function(e)
    {
        $('a.uninstall').click(this.confirmUninstall);
    };

    this.confirmUninstall = function(e)
    {
        if (confirm('Also delete this module database content?\n\n'+
                    'Note: clicking OK will remove all content '+
                    'previously generated by this module.\nClick '+
                    'Cancel to uninstall the module but leave the database content.'))
        {
            e.stopPropagation();
            e.preventDefault();
            var url = $(e.target).attr('href') + '?clean=all';
            window.location.assign(url);
        }
    };
};
$(function(){UnoAdmin.init();});
